import React, { useState, useRef } from 'react';
import './MyList.css';
import logo from './logo.jpg';


  
function deleteFun(row = {}){
  let data = this.state.data;
  data['mylist'] = data['mylist'].filter((da) => {
    return da['id'] != row['id'];
  });
  data['recommendations'].push(row);
  this.setState({
    data,
  });
}

function addFun(row = {}){
  let data = this.state.data;
  data['mylist'] = data['mylist'].filter((da) => {
    return da['id'] != row['id'];
  });
  data['recommendations'].push(row);
  this.setState({
    data,
  });
}



function Card(param = {}){
  let data = param['data'];
  let type = param['type'];
  
  return (
    <div className="card-main">
      <div>
        <span>{data['title']}</span>
      </div>
      <div>
        <img alt="" src={data['img']}/>
      </div>
      <div style="height: 150px;width: 150px;background: coral;margin-top: 20px;" id="content">
        {
          type === 'delete' ? 'delete' : 'add'
        }
      </div>
    </div>
  )
}

class MyList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: {
        mylist: [
          {
            title: 'Futurama',
            id: 1,
            img: 'http://cdn1.nflximg.net/webp/7621/3787621.webp',
          },
          {
            title: 'The Interview',
            id: 2,
            img: 'http://cdn1.nflximg.net/webp/1381/11971381.webp',
          },
          {
            title: 'Gilmore Girls',
            id: 3,
            img: 'http://cdn1.nflximg.net/webp/7451/11317451.webp',
          },
        ],
        recommendations: [
          {
            title: 'Family Guy',
            id: 4,
            img: 'http://cdn5.nflximg.net/webp/5815/2515815.webp',
          },
          {
            title: 'The Croods',
            id: 5,
            img: 'http://cdn3.nflximg.net/webp/2353/3862353.webp',
          },
          {
            title: 'Friends',
            id: 6,
            img: 'http://cdn0.nflximg.net/webp/3200/9163200.webp',
          },
        ],
      },
    };
  }

  render() {
    const { data } = this.state;
    return (
      <div className="main">
        <div>
          <span>我的第一个React页面</span>
        </div>
        <br/>
        <div>
          <img alt="" src={logo} width={100}/>
        </div>
        <br/>
        <div className="mylist">
          <div>
            <span>MyList</span>
          </div>
          <div className="card-list">
            {
              data['mylist'] && data['mylist'].map((row) => {
                return <Card data={row} type="delete"/>
              })
            }
          </div>
        </div>
        <div className="recommendations">
          <div>
            <span>Recommendations</span>
          </div>
          <div className="card-list">
            {
              data['recommendations'] && data['recommendations'].map((row) => {
                return <Card data={row} type="add"/>
              })
            }
          </div>
        </div>
      </div>
    );
  }
}

export default MyList;
